<?phpclass Laporan_model extends CI_Model {    function  __construct() {        parent::__construct();        $this->load->helper('form');    } function getreportxml($idreg) {        $this->db->join("schedules","registrations.idschedules = schedules.idschedules");        $this->db->join("users","registrations.idusers = users.idusers");        $this->db->join("branches","schedules.idbranches = branches.idbranches");        $this->db->join("exams","schedules.idexams = exams.idexams");        $this->db->where('registrations.idregistrations', $idreg);        $q =  $this->db->get("registrations");        return $q->result();    }}?>