<?php ob_start(); ?><?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); ?><?phpclass Module_model extends CI_Model {    function  __construct() {        parent::__construct();        date_default_timezone_set('Asia/Jakarta');        $this->load->helper('form');    }    function addmodule() {        $modulename = $this->input->post('modulename');        $isactive = $this->input->post('isactive');        $data = array(          'examname' => $modulename,          'examstatus' => $isactive,          'created' => date("Y-m-d H:i:s")          );        $query = $this->db->insert('exams', $data);        if($query) {          $status = array('status'=> 'sukses');          echo json_encode($status);        } else {          $status = array('status'=> 'gagal');          echo json_encode($status);        }    }    function getnewmodule() {        $this->db->limit(1);        $this->db->order_by('idexams', 'DESC');        $query = $this->db->get('exams');        return $query->result();    }    function updatemodule() {        $idexams = $this->input->post('idexams');        $modulename = $this->input->post('modulename');        $isactive = $this->input->post('isactives');                if($isactive) {          $status = 1;        } else {          $status = 0;        }        $data = array(          'examname' => $modulename,          'examstatus' => $status,        );        $this->db->where('idexams', $idexams);        $query = $this->db->update('exams', $data);          if($query) {            $status = array('status'=> 'sukses');            echo json_encode($status);          } else {            $status = array('status'=> 'gagal');            echo json_encode($status);          }    }    function deletemodule() {      $idexams = $this->input->post('idexams');            $this->db->where('idexams', $idexams);      $query = $this->db->delete('exams');        if($query) {          $status = array('status'=> $idexams);          echo json_encode($status);        }    }    }?>