<script>  $(document).ready(function() {    $('#btnaddmodule').click(function() {      $('.sticky-close').click();      $('.load').fadeIn('fast');    if(!$('.modulename').val()) {        $('.modulename').focus();        $('.load').fadeOut('fast');    } else {      var modulename = $('.modulename').val();        if($('#isactive').is(':checked')) {          var isactive = $('#isactive').val();        } else {          var isactive = '0';        }      var dataString = 'modulename=' + modulename + '&isactive=' + isactive;                var counter=2;                      var countdown = setInterval(function(){                        if (counter == 0) {                        clearInterval(countdown);                          $.ajax({                              type  : "POST",                              url: ""+base_url+"module/addmodule",                              data: dataString,                              dataType: 'json',                              success : function(data){                                $('.load').fadeOut('fast');                                 $.each( data, function(key,val) {                                   $('.modulename').val('');                                    if( val == 'sukses') {                                     $('#addmodule').modal('hide');                                     $('#sticky').sticky('<span style="color:#802222;">add a new module successfully</span>');                                     } else {                                     $('#addmodule').modal('hide');                                     $('#sticky').sticky('<span style="color:#802222;">failed to add a new module</span>');                                     }                                });                                // load content module                                 $.get( "module/getNewModule", function( data ) {                                  $(data).hide().appendTo("#list-module").effect("highlight", {color: '#feeda9'}, 2000).css({'display':'table-row'});                                });                                // load content submodule                                 $.get( "module/getNewsubModule", function( data ) {                                  $("#list-module").append(data);                                });                                  }                          });                        }                  counter--;                }, 500);      }    });                          });</script><div id="addmodule" class="modal hide fade in" style="display: none; ">            <div class="modal-header">              <a class="close" data-dismiss="modal">Ã—</a>              <h4>Add Module</h4>            </div>            <div class="modal-body">          <table>                      <tr>            <td>Module Name</td>            <td><input style="width:410px;margin-top:8px;" type="text" name="modulename" class="modulename"></td>          </tr>          <tr>            <td>Status</td>            <td>            <input type="checkbox" name="isactive"  value="1" id="isactive"  class="css-checkbox lrg" />            <label style="margin-top:4px;" for="isactive"  name="checkbox67_lbl" class="css-label lrg web-two-style"></label>            &nbsp;Is Active            </td>          </tr>          </table>            </div>            <div class="modal-footer">            <input type="submit" style="float:right;" class="btn btn-warning" id="btnaddmodule" value="Add" />             <img class="load" style="margin:0px;float:right;margin-top:5px;margin-right:17px;" src="<?php echo base_url() ?>assets/pic/load1.gif" width="25">                                     </div>  </div>  