<script type="text/javascript">  $(document).ready(function() {    $('#prosesconfirmpayment').click(function() {        $('.load').fadeIn('slow');                var counter=2;              var countdown = setInterval(function(){                if (counter == 0) {                clearInterval(countdown);                var idregs = $('.idregs').val();                var proof = $('.uploadfile').val();                var dataString = 'idreg=' + idregs + '&proof=' + proof ;                  $.ajax({                    type  : "POST",                    url: ""+base_url+"payment/confirmpayment",                    data: dataString,                         success : function(data){                     $('.load').fadeOut('slow');                                                                      // load content module                                             $.get( ""+base_url+"payment/getUpdatePayment/"+idregs+"", function( data ) {                                              $("#list-user #"+idregs+"").html(data);                                            });                                                                   $('.box-confirm').hide('fast');                                             $('.box-succesconfirm').fadeIn('slow');                                            $('#prosesconfirmpayment').hide('fast');                                                                     }                    });                    return false;            }            counter--;        }, 500);    });   });</script><div id="confirmpayment" class="modal hide fade in" style="display: none; ">          <div class="modal-header">              <a class="close" data-dismiss="modal">Ã—</a>              <h4>Upload proof of payment</h4>          </div>          <div class="modal-body" >                    <div class="box-confirm">              <table class="table" >          <tr>            <td ><div style="margin-top:7px;">ID Registrations</div></td>            <td>              <input disabled="disabled" type="text" name="idreg" class="idreg">               <input disabled="disabled" style="display:none;" type="text" name="idregs" class="idregs">            </td>          </tr>          <tr>            <td>Upload </td>                <td>                     <input type="file" onChange="JavaScript:AjaxUploads.UploadsFile();" name="uploadidcard[]" id="uploadidcard" style="display:none;" class="uploadidcard">                     <input type="hidden" name="uploadfile" class="uploadfile">                      <div name="upload" style="float:right;cursor:pointer;" class="btnuploadpayment" id="uploadidcard1" value="Upload"></div>                     <div class="resultphoto" style="float:right;margin-right:10px;"></div>                </td>          </tr>          </table>          </div>          <div class="box-succesconfirm" style="text-align:center;width:270px;margin:0px auto;">            <img src="<?php echo base_url() ?>assets/pic/succesconfirm.png" width="58px" height="60px">            <h4 style="color:#333;margin-bottom;4px;margin-top:7px;">Payment Confirmation Succesful</h4>            <p style="margin-top:7px;">Thank you for submitting your payment receipt, you will receive a confirmation email regarding your IELTS test shortly</p>          </div>          </div>                    <div class="modal-footer">            <input type="submit" style="float:right;" class="btn btn-warning" id="prosesconfirmpayment" value="Proses" />             <img class="load" style="margin:0px;float:right;margin-top:5px;margin-right:17px;" src="<?php echo base_url() ?>assets/pic/load1.gif" width="25">                                     </div>             </div> 