tion( data ) {                                              $("#list-user tr:first").after(data).effect("highlight", {color: '#feeda9'}, 2000).css({'display':'table-row'});                                            });                                            $('#formadduser input[type=text]').val('');                                            var validator = $( "#formadduser" ).validate();                                            validator.resetForm();                                          }                                          });                                        }                                });                            }                        counter--;                      }, 500);                }              });                                      $( "#formadduser" ).validate({                rules: {                  selectroles: {                    required: true                  },                  titleuser: {                    required: true                  },                  usernameuser: {                    required: true                  },                  passworduser: {                    required: true                  },                  lastnameuser: {                    required: true                  },                  firstnameuser: {                    required: true                  },                  phonenumberuser: {                    required: true                  },                  emailaddressuser: {                    required: true                  },                  addressuser: {                    required: true                  },                  cityuser: {                    required: true                  }                }            });         });           </script>